import './App.scss';
import React from 'react';

import { BrowserRouter as Router, Route, Link } from 'react-router-dom';

import TodosList from './todo-container/list-todo/ListTodo';
import CreateTodo from './todo-container/create-todo/CreateTodo';
import EditTodo from './todo-container/edit-todo/EditTodo';
import ViewTodo from './todo-container/view-todo/ViewTodo';

class App extends React.Component {

  render(){
    return (
      <Router>
        <div className="container">

          <nav className="navbar navbar-expand-lg navbar-light bg-light">
            <Link to="/" className="App-link">MERN-Stack Todo App</Link> 
            <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#myNavbar" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
              <span className="navbar-toggler-icon"></span>
            </button>
            <div className="collapse navbar-collapse" id="myNavbar">
              <ul className="navbar-nav mr-auto">
                <li className="navbar-item">
                  <Link to="/" className="nav-link">Todos</Link>
                </li>
                <li className="navbar-item">
                  <Link to="/create" className="nav-link">Create Todo</Link>
                </li>
              </ul>
            </div>

          </nav>

          <Route path='/' exact component={TodosList} />
          <Route path='/edit/:id' component={EditTodo} /> 
          <Route path='/create' component={CreateTodo} />
          <Route path='/view/:id' component={ViewTodo} />
        </div>
      </Router>
    );
  }
}

export default App;
